 		// Retorna o nº de dias de um mês num certo ano 
 		function getMonthDays(intMonth, intYear) 
 		{ 
 			switch(intMonth + 1) 
 			{ 
 				case 1: 
 				case 3: 
 				case 5: 
 				case 7: 
 				case 8: 
 				case 10: 
 				case 12: return(31); 
 				case 4: 
 				case 6: 
 				case 9: 
 				case 11: return(30); 
 				case 2: return((((intYear - 96) % 4) == 0) ? 29 : 28); 
 				default: return(-1); 
 			} 
 		} 
 	 
