 		function stringToDate(strDate, strFormat, blnForceFormat) 
 		{ 
 			var 
 				dtmReturn = new Date(), arrPartValues = [], arrDateParts = getDateParts(), intPos = 0, arrReturn, strPart; 
 				arrPartValues["mm"] = arrPartValues["dd"] = arrPartValues["yy"] = arrPartValues["yyyy"] = arrPartValues["hh"] = arrPartValues["HH"] = arrPartValues["nn"] = arrPartValues["ss"] = 0; 
 	 
 			strFormat = "dd/mm/yyyy"; 
 	 
 			if(typeof(blnForceFormat) == "undefined"){blnForceFormat = false} 
 	 
 			function getNextDatePart(strDate, intPosition, blnForceFormat, intSize) 
 			{ 
 				var arrReturn = [-1, -1], strPartValue; 
 				if((intPosition <= strDate.length) || !blnForceFormat) 
 				{ 
 					arrReturn[0] = (isNumeric(strDate.charAt(intPosition)) ? 0 : -1); 
 					strPartValue = strDate.substr(intPosition - (intSize - 1), intSize + arrReturn[0]); 
 					arrReturn[1] = new Number(strPartValue); 
 					if(isNumeric(strPartValue) || !blnForceFormat){return(arrReturn);} 
 				} 
 			} 
 	 
 			for(var i = 0; i < strFormat.length; i++) 
 			{ 
 				strPart = ""; 
 				while(strFormat.charAt(i) == strFormat.charAt(i + 1)) 
 				{ 
 					strPart += strFormat.charAt(i++); 
 				} 
 				strPart += strFormat.charAt(i); 
 	 
 				for(var j = 0; j < arrDateParts.length; j++) 
 				{ 
 	 
 					if(strPart == arrDateParts[j]) 
 					{ 
 						arrReturn = getNextDatePart(strDate, i + intPos, blnForceFormat, strPart.length); 
 						if(typeof(arrReturn) == "undefined"){return;} 
 						arrPartValues[strPart] = arrReturn[1]; 
 						intPos += arrReturn[0]; 
 						break; 
 					} 
 				} 
 			} 
 	 
 			//Este primeiro teste é para evitar dia "00" , mes "00" e anos que não podem ser inderidos no banco de dados 
 			//devido a limitação do tipo smalldatetime (limite do smalldatetime no SQL Server: de 01/01/1900 até 06/06/2079) 
 			if ( !(arrPartValues["dd"] == 0) && !(arrPartValues["mm"] == 0) && !(arrPartValues["yyyy"] < 1900 || arrPartValues["yyyy"] > 2078) ) 
 			{ 
 				if((strFormat.length >= strDate.length || !blnForceFormat) && ((arrPartValues["dd"] == 0 || arrPartValues["mm"] == 0) || arrPartValues["dd"] <= getMonthDays(arrPartValues["mm"] - 1, arrPartValues["yy"] + arrPartValues["yyyy"])) && (arrPartValues["HH"] < 24 && arrPartValues["nn"] < 60)) 
 				{ 
 					with(dtmReturn) 
 					{ 
 						setDate(arrPartValues["dd"]); 
 						setMonth(arrPartValues["mm"] - 1); 
 						(arrPartValues["yy"] > 0 ? setYear(arrPartValues["yy"]) : setFullYear(arrPartValues["yyyy"])); 
 						setSeconds(arrPartValues["ss"]); 
 						setMinutes(arrPartValues["nn"]); 
 						setHours(arrPartValues["hh"]); 
 					} 
 					return(dtmReturn); 
 				} 
 			} 
 		} 
 	 
